use std::hash::BuildHasherDefault;

pub use rustc_hash::{FxHashMap, FxHashSet, FxHasher};

pub type FxIndexMap<K, V> = indexmap::IndexMap<K, V, BuildHasherDefault<FxHasher>>;
pub type FxIndexSet<V> = indexmap::IndexSet<V, BuildHasherDefault<FxHasher>>;

#[macro_export]
macro_rules! define_id_collections {
    ($map_name:ident, $set_name:ident, $key:ty) => {
        #[doc = concat!("A hash map of [`", stringify!($key), "`]s.")]
        ///
        /// Generated by [`rustc_data_structures::define_id_collections`].
        pub type $map_name<T> = $crate::fx::FxHashMap<$key, T>;
        #[doc = concat!("A hash set of [`", stringify!($key), "`]s.")]
        ///
        /// Generated by [`rustc_data_structures::define_id_collections`].
        pub type $set_name = $crate::fx::FxHashSet<$key>;
    };
}
